import { expect } from '@hapi/code';
import Lab from '@hapi/lab';
import { configureServer } from '../src/config/server';

const { describe, it, before, after } = exports.lab = Lab.script();

describe('Authentication', () => {
  let server: any;

  before(async () => {
    server = await configureServer();
  });

  after(async () => {
    await server.stop();
  });

  it('should register a new user', async () => {
    const response = await server.inject({
      method: 'POST',
      url: '/auth/register',
      payload: {
        email: 'test@example.com',
        password: 'password123',
        name: 'Test User'
      }
    });

    expect(response.statusCode).to.equal(201);
    expect(response.result.user.email).to.equal('test@example.com');
  });

  it('should login with valid credentials', async () => {
    // First register a user
    await server.inject({
      method: 'POST',
      url: '/auth/register',
      payload: {
        email: 'login@example.com',
        password: 'password123',
        name: 'Login User'
      }
    });

    const response = await server.inject({
      method: 'POST',
      url: '/auth/login',
      payload: {
        email: 'login@example.com',
        password: 'password123'
      }
    });

    expect(response.statusCode).to.equal(200);
    expect(response.result.token).to.exist();
    expect(response.result.user.email).to.equal('login@example.com');
  });

  it('should reject invalid credentials', async () => {
    const response = await server.inject({
      method: 'POST',
      url: '/auth/login',
      payload: {
        email: 'nonexistent@example.com',
        password: 'wrongpassword'
      }
    });

    expect(response.statusCode).to.equal(401);
  });
});
